mapper.update:
update menu
set mname = #{mname,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  delmark = #{delmark,jdbcType=INTEGER}
where menuid = #{menuid,jdbcType=INTEGER}

mapper.select:
select * from menu


		and mname= #{mname}


		and url= #{url}


		and icon= #{icon}



limit #{beginIndex},#{pageSize}

	 select count(*) from menu


 				and mname= #{mname}


 				and url= #{url}


 				and icon= #{icon}
